import React from 'react';
import logo from './logo.svg';
import './App.css';
import ListItems from './ListItems'
import { library } from '@fortawesome/fontawesome-svg-core'
import { faTrash } from '@fortawesome/free-solid-svg-icons'

library.add(faTrash)

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      items:[],
      currentItem:{
      text:'',
      key:'',
      start:'',
      end:''
      }
    }


    this.addItem = this.addItem.bind(this);
    this.handleInput = this.handleInput.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
    this.setUpdate = this.setUpdate.bind(this);
    this.populateList = this.populateList.bind(this);
  }
  addItem(e){
    e.preventDefault();
    const newItem = this.state.currentItem;
    if(newItem.text !==""){
      const items = [...this.state.items, newItem];
    this.setState({
      items: items,
      currentItem:{
      text:'',
      key:'',
      start:'',
      end:''
      }
    })
    }
  }
  handleInput(e){
    this.setState({
      currentItem:{
        text: e.target.value,
        key: Date.now()
      }
    })

  }
  deleteItem(key){
    const filteredItems= this.state.items.filter(item =>
      item.key!==key);
    this.setState({
      items: filteredItems
    })

  }
  setUpdate(text, key){
    console.log("items: HELLOooo"+this.state.items);
    const tempItem = this.state.items;
    tempItem.map( x =>{      
      if(x.key===key){
        // console.log(item.key +"    "+key)
        x.text= text;
      }
    })
    this.setState({
      items: tempItem
    })
  }

   // populateList(){
   //  const startTime = this.state.start;
   //  const endTime = this.state.end;
   //    //take the two key values from the input box
   //    const total = end - start;
   //    for (let i = 0; i < total; i++){

   //    }
   // }

   setStart(e){
    this.setState({
      start: e.target.value
    })
    console.log("the start is:" + this.state)
   }


 render(){
  return (
    <div className="App">
      <header>
        <form id="to-do-form" onSubmit={this.addItem}>
          <input type="number" min="0" max="23" value = {this.state.start} onChange = {this.setStart}></input>
          <input type="number" min="0" max="23" value = {this.state.end}></input>
          <input type="text" id = "taskName" placeholder="Enter task" value= {this.state.currentItem.text} onChange={this.handleInput}></input>
          <button type="submit">Add Task</button>
        </form>
        <p>{this.state.items.text}</p>

          <ListItems items={this.state.items} deleteItem={this.deleteItem} setUpdate={this.setUpdate}/>

      </header>
    </div>
  );
 }
}


export default App;
