import React from 'react';
// import './Schedule.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import FlipMove from 'react-flip-move';

function Schedule(props){
    const schedule = props.schedule;
    console.log("strung!: " + JSON.stringify(schedule, null, 4));

    const scheduleItems = schedule.map(schedule =>
    {
      return <div className="list" key={schedule.key}>
        <p>
        <input type="checkbox" onChange={(e)=>{
             props.setUpdate(e.target.value, schedule.key)}}/>

         <input type="text" autoComplete="new-password" id={schedule.key} value={ schedule.time + ":00 | " + schedule.task} onChange={(e)=>{
             props.setUpdate(e.target.value, schedule.key)}} style = {{
                    textDecoration: schedule.completed ? "line-through" : null
                }}/>
        <span>

        <FontAwesomeIcon className="faicons" onClick={() => {
            props.deleteschedule(schedule.key)
        }} icon="trash" />
        </span>
     </p>
    </div>})

    return <div>
        <FlipMove duration={300} easing="ease-in-out">
        {scheduleItems}


        </FlipMove>
    </div>;
  }

  export default Schedule;
