import React from 'react';
// import logo from './logo.svg';
import './App.css';
import ListItems from './ListItems'
import Schedule from './Schedule'
import { library } from '@fortawesome/fontawesome-svg-core'
import { faTrash } from '@fortawesome/free-solid-svg-icons'

library.add(faTrash)

var startTime = 0;
var endTime = 0;


// const [value, setValue] = React.useState(
//     localStorage.getItem('myValueInLocalStorage') || ''
//   );

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      items:[],
      currentItem:{
        task:'',
        key:'',
        time: 0,
        completed: false
      },
      schedule: [],
      date: ""
    }
    // console.log("starting out : " + JSON.stringify(this.state.currentItem));



    this.addItem = this.addItem.bind(this);
    this.handleInput = this.handleInput.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
    this.toggleComplete = this.toggleComplete.bind(this);
    this.setUpdate = this.setUpdate.bind(this);
    this.setStart = this.setStart.bind(this);
    this.setEnd = this.setEnd.bind(this);
    this.loadItems = this.loadItems.bind(this);
    this.addSchedule = this.addSchedule.bind(this);
    this.getDate = this.getDate.bind(this);
  }

  getDate(){
    var options = {  weekday: 'long', year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: false };
    var prnDt = 'Printed on ' + new Date().toLocaleTimeString('en-us', options);

    this.setState({
      date: prnDt
    })
  }


   loadItems(){
      console.log("load items ran!");
      // this.state.items = JSON.parse(localStorage.getItem('listItems'));
      console.log("all items: " + this.state.items);
   }

    addItem(e){
      e.preventDefault();
      const newItem = this.state.currentItem;
      if(newItem.task !==""){
        const taskItems = [...this.state.items, newItem];
        this.setState({ //Resets to default
           items: taskItems,
           currentItem:{
             task:'',
             key:'',
             time: 0,
             completed: false
           }
        })

        console.log("should be the same as: " + JSON.stringify(this.state.items));
        console.log("should be the same as: " + JSON.stringify(taskItems));
        localStorage.setItem('listItems', JSON.stringify(taskItems));           
      }
    }

    addSchedule(e){
      e.preventDefault();

      //need to create a section that adds / adjusts the schedule accordingly

      const stepsArr= Array.from({length: (endTime - startTime + 1)}, (_, index) => index + 1);
      console.log("step array: " + stepsArr);


      const createdArr = stepsArr.map(step => {
        return {
          task:' ',
          key: Date.now(),
          time: parseInt(startTime) + parseInt(step) - 1,
          completed: false
        }
      })


        // console.log("temporary item :  " + JSON.stringify(tempItem, null, 4));
        console.log("created array:" + JSON.stringify(createdArr, null, 4));
        this.setState({ schedule: createdArr })
        // this.setState({ schedule: [...this.state.schedule, createdArr] })
    }

  handleInput(e){
    this.setState({
      currentItem:{
         task: e.target.value,
         key: Date.now(),
         time: startTime,
         completed: false
      }
    })

  }

  //Deletes an item from the list
  deleteItem(key){
    const filteredItems= this.state.items.filter(item =>
      item.key!==key);
    this.setState({
      items: filteredItems
    })

  }

  deleteAllItems(){
    localStorage.clear();
    this.setState({
      items: []
    })
  }

   //Toggles the completion of a task
   toggleComplete(value, key){
      const tempItem = this.state.items;
      tempItem.map( x =>{      
         if(x.key===key){ //If it is the item we are looking for 
           x.completed = !x.completed;
         }
      })
      this.setState({
         items: tempItem
      })
      console.log("current completion:" + this.state.completed);
  }

  //Updates the task 
  setUpdate(task, key){
    const tempItems = this.state.items;
    tempItems.map( x =>{      
      if(x.key===key){
        x.task = task;
        x.completed = !x.completed;  

        //For creating strikethrough
        // if(x.completed){
        //     x.textDecor='line';
        //  }
        //  else{
        //     x.textDecor = null;
        //  }
      }
    })
    this.setState({
      items: tempItems
    })  
  }

    setStart(e){
        // e.preventDefault();
        startTime = e.target.value;
        // populateList();
   }

    setEnd(e){
        // e.preventDefault();
        endTime = e.target.value;
        // populateList();
    }


 render(){
  return (
   // this.state.items = JSON.parse(localStorage.getItem('listItems'))
   <div className = "Container">
    <div className="App">
      <header>
        <p> {this.getDate}</p>
        <form id="to-do-form" onSubmit={this.addItem}>
          <input type="text" id = "taskName" placeholder="Enter task" value= {this.state.currentItem.task} onChange={this.handleInput}></input>
          <button type="submit">Add Task</button>
        </form>


        <p>{this.state.items.task}</p>
          <ListItems items={this.state.items} deleteItem={this.deleteItem} setUpdate={this.setUpdate}/>

      </header>
    </div>

    <div className="App">
     <form id="schedule" onSubmit={this.addSchedule}>
         <p> From</p>
          <input type="number" min="0" max="23" value = {this.state.currentItem.start} onChange = {this.setStart}></input>
          <p> To </p>
          <input type="number" min="0" max="23" value = {this.state.currentItem.end}  onChange = {this.setEnd}></input>
          <button type="submit">Create</button>
      </form>
        <Schedule schedule={this.state.schedule}/> 
    </div>
  </div>
  );
 }
}


export default App;
