     })

    }



  //Deletes an item from the list
  deleteItem(key){
    const filteredItems= this.state.items.filter(item =>
      item.key!==key);
    this.setState({
      items: filteredItems
    })

  }

  //Deletes an item from the schedule
  deleteScheduleItem(key){
    console.log("in the deleteScheduleItem method");
